    private void createTestDestinations() {
        if (context != null) {
            DestinationController destinationController = context.getBean(DestinationController.class);
            GeographyController geographyController = context.getBean(GeographyController.class);
            TripItemController tripItemController = context.getBean(TripItemController.class);
            HotelController hotelController = context.getBean(HotelController.class);

            for (City city : geographyController.getAllCities()) {
                Destination destination = new Destination(city);

                Optional<Hotel> hotel = hotelController.getFirstByAddressCityName(city.getName());
                if (hotel.isPresent()) {
                    destination.setHotel(hotel.get());

                    Optional<List<TripItem>> tripItems = tripItemController.getByCityName(city.getName());
                    if (tripItems.isPresent() && !tripItems.get().isEmpty()) {
                        destination.setTripItems(tripItems.get());
                    }

                    destinationController.createDestination(destination);
                }
            }
        }
    }

