    private void createTestTravels() {
        if (context != null) {
            Optional<List<Trip>> trips = context.getBean(TripController.class).getAllTrips(1);
            if (trips.isPresent()) {
                Travel travel = new Travel(trips.get().get(0));

                List<TripItem> tripItems = new ArrayList<>();// trip.getTripItems();
                travel.setTripItems(tripItems);

                context.getBean(TravelController.class).createTravel(travel);
            }
        }
    }

    private void createTestTravelGroups() {
        if (context != null) {
            String[] names = {"50+ Reisgroep", "De Daarduivels", "Beni 'fucking bloody' dorm Bastards",
                    "De vriendloze", "De 5-vijvers"};

            UserController userController = context.getBean(UserController.class);
            TravelGroupController travelGroupController = context.getBean(TravelGroupController.class);

            String username = "user";

            for (String name : names) {
                Optional<User> user = userController.getUserByUsername(username);

                if (user.isPresent()) {
                    Optional<TravelGroup> travelGroup = travelGroupController.createTravelGroup(name, user.get().getId());
                    travelGroup.ifPresent(group -> userController.addTravelGroup(group, user.get().getId()));
                }
            }
        }
    }
